INSERT INTO book (name, photo, description, id_lang, date_create, id_publisher, id_style, id_binding, date_public) VALUES('Белый отряд', 'Дизайн-без-названия-52.jpg', 'Женщина всю жизнь работала в колхозе. Детей и мужа она потеряла, но сохранила доброту и бескорыстно всем помогала. После её нелепой и страшной смерти родные делят её дом и вспоминают о ней с укором.', 25, 1804, 37, 17, 1, 2017);
