INSERT INTO book (name, photo, description, id_lang, date_create, id_publisher, id_style, id_binding, date_public) VALUES('Бегство от свободы', 'Успеть-до-восхода.png', 'Рассказчик смотрит на школьную фотографию и вспоминает о друге детства, бабушке, родной избе, раскула­чивании, деревенском быте и семье молодых учителей, которые организовали школу в его глухом селе.', 19, 1914, 60, 21, 1, 2011);
INSERT INTO book (name, photo, description, id_lang, date_create, id_publisher, id_style, id_binding, date_public) VALUES('Алюминий. Тринадцатый элемент', 'Без названия.jpg', 'Известный учёный превратил пса в человека. Тот оказался редким мерзавцем: хамил, напивался, приставал к женщинам, требовал жилплощадь и писал доносы. Учёный не выдержал и превратил его обратно в пса.', 19, 1968, 42, 8, 2, 2011);
