INSERT INTO book (name, photo, description, id_lang, date_create, id_publisher, id_style, id_binding, date_public) VALUES('Билет, который лопнул', '10-sm.jpg', 'Учитель гимназии, боящийся всего на свете и живущий согласно распоряжениям начальства, решает жениться. Долгое сватовство извлекает учителя из его «футляра», и он умирает, испугавшись реальной жизни.', 26, 1856, 5, 17, 2, 2010);
INSERT INTO book (name, photo, description, id_lang, date_create, id_publisher, id_style, id_binding, date_public) VALUES('Алматы (энциклопедия)', 'cover_3.jpg', 'Известный учёный превратил пса в человека. Тот оказался редким мерзавцем: хамил, напивался, приставал к женщинам, требовал жилплощадь и писал доносы. Учёный не выдержал и превратил его обратно в пса.', 1, 1800, 62, 14, 1, 2011);
