INSERT INTO book (book_name, book_photo, book_description, book_lang_id, book_date, book_publisher_id, book_style_id, book_binding_id, book_date_public) VALUES('Анекдоты о бунтовщике и самозванце Емельке Пугачёве', 'maxresdefault.jpg', 'У старика сжималось сердце при виде выброшенных сломанных кукол без рук и ног: за годы войны он «нагляделся человечины». Найдя куклу, над которой жестоко надругались, он хоронит её как человека.', 22, 1957, 6, 12, 2, 2017);
